import telebot
import config
import random
from telebot import types

bot = telebot.AsyncTeleBot(config.TOKEN)

@bot.message_handler(commands=['start'])
def welcome(message):
	bot.send_message(message.chat.id, "Привет, любимая!".format(message.from_user, bot.get_me()), parse_mode='html')

	markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
	item1 = types.KeyboardButton("Хочу комплиент!")
	item2 = types.KeyboardButton("Хочу фильм!")
	item3 = types.KeyboardButton("Я в загоне(")
	item4 = types.KeyboardButton("Истерика!")

	markup.add(item1, item2, item3)
	bot.send_message(message.chat.id, "Дашунь, этот бот не особо умный (потому что его создатель тоже), так что все, что тебе нужно это нажимать на кнопку. Писать ему что-то свое бессмысленно, он не поймет. Целую!".format(message.from_user, bot.get_me()), parse_mode='html', reply_markup=markup)



